from django.http import HttpResponse, Http404from django.shortcuts import render_to_responsefrom django.template import Context, loaderfrom django.template import RequestContextfrom django.views.decorators.http import require_POSTfrom c2g.models import *from courses.common_page_data import get_common_page_datafrom courses.actions import auth_view_wrapper, auth_is_course_admin_view_wrapperimport urllib2@auth_view_wrapperdef view(request, course_prefix, course_suffix):    try:        common_page_data = get_common_page_data(request, course_prefix, course_suffix)    except:        raise Http404        return render_to_response('webinar/view.html', {'common_page_data':common_page_data}, context_instance=RequestContext(request))		# A request for webinar meetingsdef get_recordings(request):	action = request.GET['action']	base_url = "http://cec-online.auca.kg/bigbluebutton/recordings.php?action="		url = base_url + action		# If getUrl, provide the recording Id to retrieve the specific url	# By default, all are returned (arg: getAll)	if action == "getUrl":		url = url + "&recordId=" + request.GET['recordId']		resp = urllib2.urlopen(url)	return HttpResponse(resp.read())	# A request for webinar meetingsdef get_meetings(request):	action = request.GET['action']	base_url = "http://cec-online.auca.kg/bigbluebutton/meetings.php?action="		url = base_url + action		# If getUrl, provide the recording Id to retrieve the specific url	# By default, all are returned (arg: getRunningMeetings)	# if action == "getUrl":		# url = url + "&recordId=" + request.GET['recordId']		resp = urllib2.urlopen(url)	return HttpResponse(resp.read())	